link_ode
# include <cstring> # include <cppad/vector.hpp> # include <cppad/speed/ode_evaluate.hpp> # include <cppad/speed/uniform_01.hpp>
bool link_ode( size_t size , size_t repeat , CppAD::vector<double> &x , CppAD::vector<double> &jacobian ) { // ------------------------------------------------------------- // setup
size_t n = size; assert( x.size() == n );
size_t m = 0; CppAD::vector<double> f(n);
while(repeat--) { // choose next x value uniform_01(n, x);
// evaluate function CppAD::ode_evaluate(x, m, f);
} size_t i; for(i = 0; i < n; i++) jacobian[i] = f[i]; return true; }
